import React, { useEffect, useState } from 'react';
import { busca } from '../api/api'
import { Link } from 'react-router-dom'

// card para mostrar conteudo do post

const ListaPost = ( {url} ) => {

    const [posts, setPosts] = useState([]);  //Stado inicial da aplicacao
    useEffect( () => { // faz a busca e atualiza o stado da aplicacao
        busca(url, setPosts);
    }, [] );    

    return(
        <section className="posts container"> 
            {
                posts.map( (post) => (
                    <Link className={`cartao-post cartao-post--${post.categoria}`} to={`/posts/${post.id}`}>
                        <article key={post.id}>
                            <h3 className="cartai-post__titulo"> {post.title} </h3>
                            <p className="cartai-post__meta"> { post.metadescription} </p>
                        </article>
                    </Link>
                ))
            }
        </section>
    )
}

export default ListaPost
