import React, { useState, useEffect } from 'react';
import { busca } from '../api/api'
import '../assets/css/blog.css'
import { Link } from 'react-router-dom';

const ListaCategorias = () => {

    const [categorias, setCategorias] = useState({});  //Stado inicial da aplicacao
    useEffect( () => { // faz a busca e atualiza o stado da aplicacao
        busca(`/categorias`, setCategorias)
    }, [] ); 
    return(
        <ul className="lista-categorias container flex">
            {
                categorias.map( (categorias) => {
                    <Link to={`categorias/${categorias.id}`}>
                        <li className={`lista-categotias__categoria lista-categorias__categoria--${categoria.id}`}></li>
                    </Link>
                })
            }

        </ul>
    )
}

export default ListaCategorias
